query ZarazActionsSparklinesDataByDate(
  $zoneTag: string
  $start: Date
  $end: Date
) {
  viewer {
    zones(filter: { zoneTag: $zoneTag }) {
      actions: zarazActionsAdaptiveGroups(
        limit: 10000
        filter: {
          datetimeHour_geq: $start
          datetimeHour_leq: $end
          toolName: $toolId
        }
        orderBy: [datetimeHour_ASC]
      ) {
        count
        dimensions {
          ts: datetimeHour
          name: actionName
        }
      }
    }
  }
}

query ZarazSparklinesDataByDate($zoneTag: string, $start: Date, $end: Date) {
  viewer {
    zones(filter: { zoneTag: $zoneTag }) {
      triggers: zarazTriggersAdaptiveGroups(
        limit: 10000
        filter: { datetimeHour_geq: $start, datetimeHour_leq: $end }
        orderBy: [datetimeHour_ASC]
      ) {
        count
        dimensions {
          ts: datetimeHour
          name: triggerName
        }
      }
      tools: zarazActionsAdaptiveGroups(
        limit: 10000
        filter: { datetimeHour_geq: $start, datetimeHour_leq: $end }
        orderBy: [datetimeHour_ASC]
      ) {
        count
        dimensions {
          ts: datetimeHour
          name: toolName
        }
      }
    }
  }
}
